import { createTheme } from '@mui/material/styles';

const theme = createTheme({
    palette: {
        mode: 'light',
        primary: {
            main: '#1A237E', // deep blue
            contrastText: '#FFFFFF',
        },
        secondary: {
            main: '#00B8A9', // teal
            contrastText: '#FFFFFF',
        },
        accent: {
            main: '#FF4081', // vibrant magenta
        },
        warning: {
            main: '#FFB300', // orange
        },
        success: {
            main: '#C6FF00', // lime
        },
        background: {
            default: '#F9FAFB', // soft off-white
            paper: '#FFFFFF', // white cards
        },
        text: {
            primary: '#212121', // dark gray
            secondary: '#757575',
        },
    },
    typography: {
        fontFamily: 'Inter, Arial, sans-serif',
        h3: {
            fontWeight: 700,
            letterSpacing: '-1px',
            textAlign: 'center',
        },
        h4: {
            fontWeight: 600,
            textAlign: 'center',
        },
        h5: {
            fontWeight: 600,
            textAlign: 'center',
        },
        h6: {
            textAlign: 'center',
        },
        body1: {
            textAlign: 'center',
        },
        body2: {
            textAlign: 'center',
        },
    },
    components: {
        MuiCard: {
            styleOverrides: {
                root: {
                    borderRadius: 24,
                    boxShadow: '0 4px 24px rgba(26,35,126,0.10)',
                    border: '2px solid #00B8A9',
                    background: 'linear-gradient(135deg, #F9FAFB 0%, #E3F2FD 100%)',
                },
            },
        },
        MuiButton: {
            styleOverrides: {
                root: {
                    borderRadius: 16,
                    fontWeight: 600,
                },
            },
        },
        MuiDrawer: {
            styleOverrides: {
                paper: {
                    background: 'linear-gradient(135deg, #1A237E 0%, #00B8A9 100%)',
                    color: '#FFFFFF',
                },
            },
        },
    },
});

export default theme;
