import { createTheme } from '@mui/material/styles';

const theme = createTheme({
    palette: {
        mode: 'light',
        primary: {
            main: '#1A237E', // deep blue
            contrastText: '#FFFFFF',
        },
        secondary: {
            main: '#00B8A9', // teal
            contrastText: '#FFFFFF',
        },
        accent: {
            main: '#FFB300', // orange
        },
        background: {
            default: '#F4F6F8', // light gray
            paper: '#FFFFFF', // white cards
        },
        text: {
            primary: '#212121', // dark gray
            secondary: '#757575',
        },
    },
    typography: {
        fontFamily: 'Inter, Arial, sans-serif',
        h3: {
            fontWeight: 700,
            letterSpacing: '-1px',
        },
        h5: {
            fontWeight: 600,
        },
    },
    components: {
        MuiCard: {
            styleOverrides: {
                root: {
                    borderRadius: 20,
                    boxShadow: '0 2px 12px rgba(26,35,126,0.08)',
                },
            },
        },
        MuiButton: {
            styleOverrides: {
                root: {
                    borderRadius: 12,
                },
            },
        },
        MuiDrawer: {
            styleOverrides: {
                paper: {
                    backgroundColor: '#1A237E',
                    color: '#FFFFFF',
                },
            },
        },
    },
});

export default theme;
