import React, { useState } from 'react';
import { ThemeProvider } from '@mui/material/styles';
import theme from './theme';
import { Container, Typography, Button, Box, CircularProgress, Card, CardContent, Avatar, Link, Grid } from '@mui/material';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import { Bar } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function App() {
    const [file, setFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [results, setResults] = useState(null);
    const [error, setError] = useState('');

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
        setError('');
    };

    const handleUpload = async () => {
        if (!file) {
            setError('Please select a file.');
            return;
        }
        setLoading(true);
        setError('');
        const formData = new FormData();
        formData.append('file', file);
        try {
            const response = await fetch('http://localhost:5000/upload', {
                method: 'POST',
                body: formData,
            });
            const data = await response.json();
            setResults(data);
        } catch (err) {
            setError('Upload failed.');
        }
        setLoading(false);
    };

    // Chart data for top tracks
    const trackChartData = results ? {
        labels: results.top_tracks.map(t => t.name),
        datasets: [
            {
                label: 'Play Count',
                data: results.top_tracks.map(t => t.count),
                backgroundColor: '#AEE6FA',
                borderColor: '#181C2A',
                borderWidth: 2,
            },
        ],
    } : null;

    // Chart data for top artists
    const artistChartData = results ? {
        labels: results.top_artists.map(a => a.name),
        datasets: [
            {
                label: 'Play Count',
                data: results.top_artists.map(a => a.count),
                backgroundColor: '#F5E3DA',
                borderColor: '#181C2A',
                borderWidth: 2,
            },
        ],
    } : null;

    // Sentiment chart for top tracks
    const sentimentChartData = results ? {
        labels: results.top_tracks.map(t => t.name),
        datasets: [
            {
                label: 'Sentiment Polarity',
                data: results.sentiment_scores,
                backgroundColor: results.sentiment_scores.map(s => s > 0.2 ? '#AEE6FA' : s < -0.2 ? '#F5E3DA' : '#B8E3F6'),
                borderColor: '#181C2A',
                borderWidth: 2,
            },
        ],
    } : null;

    const chartOptions = {
        responsive: true,
        plugins: {
            legend: { display: false },
            title: { display: false },
        },
        scales: {
            x: {
                ticks: { color: '#FFFFFF' },
                grid: { color: '#23263A' },
            },
            y: {
                ticks: { color: '#FFFFFF' },
                grid: { color: '#23263A' },
            },
        },
    };

    return (
        <ThemeProvider theme={theme}>
            <Box minHeight="100vh" sx={{ bgcolor: 'background.default', py: 8 }}>
                <Container maxWidth="md" sx={{ bgcolor: 'background.paper', borderRadius: 4, boxShadow: 3, py: 4 }}>
                    <Typography variant="h3" align="center" gutterBottom color="primary.main">
                        Spotify Wrapped, But Better
                    </Typography>
                    <Box display="flex" flexDirection="column" alignItems="center" gap={2}>
                        <Button
                            variant="contained"
                            component="label"
                            startIcon={<CloudUploadIcon />}
                            color="primary"
                            sx={{ fontWeight: 600 }}
                        >
                            Upload StreamingHistory.json
                            <input type="file" hidden onChange={handleFileChange} />
                        </Button>
                        {file && <Typography color="secondary.main">{file.name}</Typography>}
                        <Button
                            variant="contained"
                            color="secondary"
                            onClick={handleUpload}
                            disabled={loading}
                            sx={{ fontWeight: 600 }}
                        >
                            Analyze
                        </Button>
                        {loading && <CircularProgress color="secondary" />}
                        {error && <Typography color="error.main">{error}</Typography>}
                        {results && (
                            <Box mt={4} width="100%">
                                {/* Top Tracks Chart */}
                                <Card sx={{ mb: 4, bgcolor: 'secondary.main', color: 'background.default' }}>
                                    <CardContent>
                                        <Typography variant="h5" gutterBottom color="background.default">Top Tracks</Typography>
                                        <Box sx={{ mb: 3 }}>
                                            <Bar data={trackChartData} options={chartOptions} height={200} />
                                        </Box>
                                        <Grid container spacing={2}>
                                            {results.top_tracks.map((track, i) => (
                                                <Grid item xs={12} sm={6} md={4} key={i}>
                                                    <Card variant="outlined" sx={{ display: 'flex', alignItems: 'center', p: 1, bgcolor: 'background.paper' }}>
                                                        <Avatar
                                                            variant="square"
                                                            src={track.album_art}
                                                            alt={track.name}
                                                            sx={{ width: 56, height: 56, mr: 2 }}
                                                        />
                                                        <Box>
                                                            <Typography variant="subtitle1">
                                                                {track.spotify_url ? (
                                                                    <Link href={track.spotify_url} target="_blank" rel="noopener noreferrer" color="primary.main">
                                                                        {track.name}
                                                                    </Link>
                                                                ) : track.name}
                                                            </Typography>
                                                            <Typography variant="body2" color="text.secondary">
                                                                {track.artist}
                                                            </Typography>
                                                            <Typography variant="caption">Plays: {track.count}</Typography>
                                                        </Box>
                                                    </Card>
                                                </Grid>
                                            ))}
                                        </Grid>
                                    </CardContent>
                                </Card>
                                {/* Top Artists Chart */}
                                <Card sx={{ mb: 4, bgcolor: 'secondary.main', color: 'background.default' }}>
                                    <CardContent>
                                        <Typography variant="h5" gutterBottom color="background.default">Top Artists</Typography>
                                        <Box sx={{ mb: 3 }}>
                                            <Bar data={artistChartData} options={chartOptions} height={200} />
                                        </Box>
                                        <Grid container spacing={2}>
                                            {results.top_artists.map((artist, i) => (
                                                <Grid item xs={12} sm={6} md={4} key={i}>
                                                    <Card variant="outlined" sx={{ display: 'flex', alignItems: 'center', p: 1, bgcolor: 'background.paper' }}>
                                                        <Avatar
                                                            src={artist.image}
                                                            alt={artist.name}
                                                            sx={{ width: 56, height: 56, mr: 2 }}
                                                        />
                                                        <Box>
                                                            <Typography variant="subtitle1">
                                                                {artist.spotify_url ? (
                                                                    <Link href={artist.spotify_url} target="_blank" rel="noopener noreferrer" color="primary.main">
                                                                        {artist.name}
                                                                    </Link>
                                                                ) : artist.name}
                                                            </Typography>
                                                            <Typography variant="caption">Plays: {artist.count}</Typography>
                                                        </Box>
                                                    </Card>
                                                </Grid>
                                            ))}
                                        </Grid>
                                    </CardContent>
                                </Card>
                                {/* Sentiment Chart */}
                                <Card sx={{ mb: 4, bgcolor: 'background.paper' }}>
                                    <CardContent>
                                        <Typography variant="h5" color="primary.main">Sentiment</Typography>
                                        <Typography sx={{ mb: 2 }}>{results.sentiment}</Typography>
                                        <Bar data={sentimentChartData} options={{
                                            ...chartOptions,
                                            scales: {
                                                ...chartOptions.scales,
                                                y: {
                                                    ...chartOptions.scales.y,
                                                    min: -1,
                                                    max: 1,
                                                    title: { display: true, text: 'Polarity (-1 = Negative, 1 = Positive)', color: '#FFFFFF' }
                                                }
                                            }
                                        }} height={150} />
                                    </CardContent>
                                </Card>
                                <Card sx={{ bgcolor: 'background.paper' }}>
                                    <CardContent>
                                        <Typography variant="h5" color="primary.main">Recommendations</Typography>
                                        {results.recommendations.length === 0 ? (
                                            <Typography>No recommendations yet.</Typography>
                                        ) : (
                                            <Grid container spacing={2} mt={1}>
                                                {results.recommendations.map((artist, i) => (
                                                    <Grid item xs={12} sm={6} md={4} key={i}>
                                                        <Card variant="outlined" sx={{ display: 'flex', alignItems: 'center', p: 1, bgcolor: 'secondary.main' }}>
                                                            <Avatar
                                                                src={artist.image}
                                                                alt={artist.name}
                                                                sx={{ width: 56, height: 56, mr: 2 }}
                                                            />
                                                            <Box>
                                                                <Typography variant="subtitle1">
                                                                    {artist.spotify_url ? (
                                                                        <Link href={artist.spotify_url} target="_blank" rel="noopener noreferrer" color="primary.main">
                                                                            {artist.name}
                                                                        </Link>
                                                                    ) : artist.name}
                                                                </Typography>
                                                            </Box>
                                                        </Card>
                                                    </Grid>
                                                ))}
                                            </Grid>
                                        )}
                                    </CardContent>
                                </Card>
                            </Box>
                        )}
                    </Box>
                </Container>
            </Box>
        </ThemeProvider>
    );
}

export default App;
