import { createTheme } from '@mui/material/styles';

// Color Hunt palette: #F0F0F0, #213555, #4F709C, #E5D283
const theme = createTheme({
    palette: {
        mode: 'light',
        primary: {
            main: '#213555', // deep blue
            contrastText: '#F0F0F0',
        },
        secondary: {
            main: '#4F709C', // blue-gray
            contrastText: '#F0F0F0',
        },
        accent: {
            main: '#E5D283', // gold
        },
        background: {
            default: '#F0F0F0', // light gray
            paper: '#FFFFFF', // white cards
        },
        text: {
            primary: '#213555', // deep blue for text
            secondary: '#4F709C',
        },
        warning: {
            main: '#E5D283',
        },
    },
    typography: {
        fontFamily: 'Inter, Arial, sans-serif',
        h3: {
            fontWeight: 700,
            letterSpacing: '-1px',
            textAlign: 'center',
        },
        h4: {
            fontWeight: 600,
            textAlign: 'center',
        },
        h5: {
            fontWeight: 600,
            textAlign: 'center',
        },
        h6: {
            textAlign: 'center',
        },
        body1: {
            textAlign: 'center',
        },
        body2: {
            textAlign: 'center',
        },
    },
    components: {
        MuiCard: {
            styleOverrides: {
                root: {
                    borderRadius: 24,
                    boxShadow: '0 4px 24px rgba(33,53,85,0.10)',
                    border: '2px solid #4F709C',
                    background: 'linear-gradient(135deg, #F0F0F0 0%, #E5D283 100%)',
                },
            },
        },
        MuiButton: {
            styleOverrides: {
                root: {
                    borderRadius: 16,
                    fontWeight: 600,
                },
            },
        },
        MuiDrawer: {
            styleOverrides: {
                paper: {
                    background: 'linear-gradient(135deg, #213555 0%, #4F709C 100%)',
                    color: '#F0F0F0',
                },
            },
        },
    },
});

export default theme;
