import { createTheme } from '@mui/material/styles';

// Color Hunt palette: #F0F0F0, #213555, #4F709C, #E5D283
const theme = createTheme({
    palette: {
        mode: 'light',
        primary: {
            main: '#223A5F', // deep blue
            contrastText: '#F6F8FC',
        },
        secondary: {
            main: '#3BA99C', // teal
            contrastText: '#F6F8FC',
        },
        accent: {
            main: '#FFD166', // gold
        },
        background: {
            default: '#F6F8FC', // soft background
            paper: '#FFFFFF', // white cards
        },
        text: {
            primary: '#223A5F',
            secondary: '#3BA99C',
        },
        warning: {
            main: '#FFD166',
        },
    },
    typography: {
        fontFamily: 'Inter, Arial, sans-serif',
        h3: {
            fontWeight: 700,
            letterSpacing: '-1px',
            textAlign: 'center',
        },
        h4: {
            fontWeight: 600,
            textAlign: 'center',
        },
        h5: {
            fontWeight: 600,
            textAlign: 'center',
        },
        h6: {
            textAlign: 'center',
        },
        body1: {
            textAlign: 'center',
        },
        body2: {
            textAlign: 'center',
        },
    },
    components: {
        MuiCard: {
            styleOverrides: {
                root: {
                    borderRadius: 28,
                    boxShadow: '0 6px 32px rgba(34,58,95,0.12)',
                    border: '2px solid #3BA99C',
                    background: 'linear-gradient(135deg, #F6F8FC 0%, #FFD166 100%)',
                    transition: 'box-shadow 0.2s, transform 0.2s',
                    '&:hover': {
                        boxShadow: '0 12px 48px rgba(34,58,95,0.18)',
                        transform: 'translateY(-4px) scale(1.03)',
                    },
                },
            },
        },
        MuiButton: {
            styleOverrides: {
                root: {
                    borderRadius: 18,
                    fontWeight: 600,
                },
            },
        },
        MuiDrawer: {
            styleOverrides: {
                paper: {
                    background: 'linear-gradient(135deg, #223A5F 0%, #3BA99C 100%)',
                    color: '#F6F8FC',
                },
            },
        },
    },
});

export default theme;
