import { createTheme } from '@mui/material/styles';

const theme = createTheme({
    palette: {
        mode: 'dark',
        background: {
            default: '#181C2A', // dark navy
            paper: '#23263A',   // slightly lighter navy for cards
        },
        primary: {
            main: '#AEE6FA',    // light blue accent
            contrastText: '#181C2A',
        },
        secondary: {
            main: '#F5E3DA',    // soft beige/pink
            contrastText: '#181C2A',
        },
        text: {
            primary: '#FFFFFF',
            secondary: '#B8E3F6',
        },
    },
    typography: {
        fontFamily: 'Inter, Arial, sans-serif',
        h3: {
            fontWeight: 700,
            letterSpacing: '-1px',
        },
        h5: {
            fontWeight: 600,
        },
    },
    components: {
        MuiCard: {
            styleOverrides: {
                root: {
                    background: '#23263A',
                    borderRadius: 20,
                },
            },
        },
        MuiButton: {
            styleOverrides: {
                root: {
                    borderRadius: 12,
                },
            },
        },
    },
});

export default theme;
